import React from 'react';
import { fetchCards} from '../api';

const SearchBar = (props) => {
    console.log(props);

    function searchCards(event) {
        // prevent default
        event.preventDefault();
        // grab text inside of input
        // call my api (fetch cards and pass in the user form input
        // call set results so that other components know of the change of state 
    }

    return (
        <div id="search">
        <h3>Look up cards here...</h3>
        <form>
            <input type="text" placeholder="card search" />
            <button type="submit" onClick={fetchCards}>Search</button>
        </form>
        </div>
    )
}

export default SearchBar;
